import { Component } from 'react';

class searchCityForm extends Component {
    state = { cities: [],
        initialLetter:'',
        submit: false,
        alert: '' }

        onClear = () => {
            this.setState({initialLetter:'',cities:[]})
          }    

    onSubmit = (e) =>{
        e.preventDefault();
           if(this.state.initialLetter === ''){
              this.setState({alert: 'Enter some letter to Search city'});
           } else {
           this.searchCities(this.state.initialLetter);
           }console.log(this.state.initialLetter) 
      }
      onChange = async (e) =>{
        this.setState({initialLetter:e.target.value})
        if(e.target.value!== ''){
        const res = await axios.get(
          `http://localhost:8080/city?letter=${e.target.value}`
        );
        this.setState({
          cities: res.data.list,
        });
        console.log(res.data);
      }
      }
      searchCities = async () => {
        const res = await axios.get(
          `http://localhost:8080/city?letter=${this.state.initialLetter}`
        );
        this.setState({
          cities: res.data.list,
        });
        console.log(res.data);
      };
    render() {
        return (
            <div className="searchComponent">
                <form className='form' onSubmit={this.onSubmit}>
                    <input type='text' name='text' value={this.state.initialLetter} onChange={this.onChange} placeholder='Search'/>
                    <input type='submit' name='submit' className='btn btn-dark btn-block' />
                    {this.state.initialLetter !== '' &&
                    <input type='button' name='clear' onClick={this.onClear} value='Clear' className='btn btn-dark btn-block' />
    }
                </form>
               { this.state.initialLetter !== '' && this.state.cities !== [] && this.state.cities !== null &&
      <h1>Number of cities starting with {this.state.initialLetter} is {this.state.cities.length}
      {this.state.cities.map(city => <div>{city.name}</div>)} </h1> 
  }
  {this.state.initialLetter === '' && this.state.alert && <div className="AlertBox"><h1>{this.state.alert}</h1></div>}
            </div>

        );
    }
}

export default searchCityForm;
